/** * $Id: FlabotLauncherTabGroup.java,v 1.4 2006/03/02 21:36:27 dacostae Exp $ * $Author: dacostae $ */package org.isistan.flabot.launcher.tabs;
import org.eclipse.debug.ui.AbstractLaunchConfigurationTabGroup;import org.eclipse.debug.ui.ILaunchConfigurationDialog;import org.eclipse.debug.ui.ILaunchConfigurationTab;import org.isistan.flabot.launcher.tabs.basic.MessageLaunchConfigurationTab;/** * Tab group to establish a Flabot LaunchConfiguration * @author usuario * */public class FlabotLauncherTabGroup extends AbstractLaunchConfigurationTabGroup {	/**	 * Creates the tabs	 */	public void createTabs(ILaunchConfigurationDialog dialog, String mode) {		DelegatingConfigurationTab collectionLauncherConfigurationTab=new DelegatingConfigurationTab(				"Collection Launcher Configuration", null, new MessageLaunchConfigurationTab());		DelegatingConfigurationTab contextProviderConfigurationTab=new DelegatingConfigurationTab(				"Context Provider Configuration", null, new MessageLaunchConfigurationTab());				ILaunchConfigurationTab[] tabs=new ILaunchConfigurationTab[] {				new MainConfigurationTab(collectionLauncherConfigurationTab, contextProviderConfigurationTab),				collectionLauncherConfigurationTab,				contextProviderConfigurationTab		};		setTabs(tabs);	}}